package loops;

public class For_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//start at-->2 	ends at-->20  	increment by -->a=a=+2
		for(int a=2;a<=20;a=a+2) {
			System.out.println(a);
		}
		System.out.println("===================================================");
		for(int a=20;a>=2;a=a-2) {
			System.out.println(a);
		}
		System.out.println("===================================================");
		
		for(int b=3;b<=30;b=b+3)
		{
			System.out.println(b);
		}
		System.out.println("=====================================================");
		for(int b=30;b>=3;b=b-3) {
			System.out.println(b);
		}
		System.out.println("=====================================================");
		for(int c=4;c<=40;c=c+4) {
			System.out.println(c);
		}
		System.out.println("=====================================================");
		for(int c=40;c>=4;c=c-4) {
			System.out.println(c);
		}
		System.out.println("=====================================================");
		
		for(int d=5;d<=50;d=d+5) {
			System.out.println(d);
		}
		System.out.println("=====================================================");
		for(int d=50;d>=5;d=d-5) {
			System.out.println(d);
		}
		System.out.println("=====================================================");
		for(int e=6;e<=60;e=e+6) {
			System.out.println(e);
		}
		System.out.println("=====================================================");
		for(int e=60;e>=6;e=e-6) {
			System.out.println(e);
		}

	}

}
